<template>  <div>    <v-data-table        :headers="headers"        :items="items"        class="elevation-1"        :loading="isLoading"    >      <template v-slot:top>        <v-toolbar flat>          <v-toolbar-title>Items List</v-toolbar-title>          <v-divider class="mx-4" inset vertical></v-divider>          <v-spacer></v-spacer>          <v-dialog v-model="dialog" max-width="500px">            <template v-slot:activator="{ on, attrs }">              <v-btn color="primary" v-bind="attrs" v-on="on">                Add Item              </v-btn>            </template>            <v-card>              <v-card-title>                <span class="text-h5">Details</span>              </v-card-title>              <v-card-text>                <v-text-field v-model="newItem.title" label="Name"></v-text-field>                <v-textarea v-model="newItem.description" label="Description"></v-textarea>                <v-text-field type="number" v-model.number="newItem.price" label="Price"></v-text-field>              </v-card-text>              <v-card-actions>                <v-spacer></v-spacer>                <v-btn color="blue" text @click="close">                  Cancel                </v-btn>                <v-btn color="blue" text @click="save">                  Save                </v-btn>              </v-card-actions>            </v-card>          </v-dialog>        </v-toolbar>      </template>      <template v-slot:item.price="{ item }">        <span>â‚¬ {{ item.price }}</span>      </template>    </v-data-table>  </div></template><script>import {api_get, api_post} from "../plugins/network";export default {  name: "Items",  data: function () {    return {      headers: [        {text: 'Id', value: 'id'},        {text: 'Title', value: 'title'},        {text: 'Description', value: 'description'},        {text: 'Price', value: 'price'},      ],      isLoading: true,      newItem: {        title: '',        description: '',        price: 0.0,      },      dialog: false,      items: [],    }  },  created() {    this.load(true);    this.load_data();    this.load(false);  },  methods: {    async load_data() {      this.items = await api_get('/api/items');    },    close() {      this.dialog = false    },    async save() {      this.load(true);      await api_post('/api/items', this.newItem);      this.close()      // reset new item      this.newItem = {        title: '',        description: '',        price: 0,      };      await this.load_data();      this.load(false);    },    load(state) {      this.isLoading = state;    }  }}</script><style scoped></style>